// Helper functions
export const getChapterData = (chapterNumber) => {
  return chaptersData[chapterNumber] || null;
};

export const getAllChapters = () => {
  return Object.keys(chaptersData).map(key => ({
    num: parseInt(key),
    ...chaptersData[key]
  }));
};
